mapper.select:
SELECT salary_id
FROM  teachers
WHERE name = #{name};

SELECT salary_id, name, gender, office, birthday, race, identity, hometown, politics_status, join_time, join_school_time, join_job_time, job, job_status, authorized, on_status, department, join_reason, attendance_category, job_level, administrative_post, prof_and_tech_post, special_experience, last_edu_background, degree, degree_time, last_degree, subject, remark, mentor_type, major, post_type, graduate_province FROM teachers WHERE
${key} = #{value}
ORDER BY name;

SELECT salary_id, name, gender, office, birthday, race, identity, hometown, politics_status, join_time, join_school_time, join_job_time, job, job_status, authorized, on_status, department, join_reason, attendance_category, job_level, administrative_post, prof_and_tech_post, special_experience, last_edu_background, degree, degree_time, last_degree, subject, remark, mentor_type, major, post_type, graduate_province FROM teachers WHERE
${key} LIKE '%${value}%'
ORDER BY name;
